function debounce(callback,wait){let timeoutId=null;return function(...arguments_){if(timeoutId)clearTimeout(timeoutId);const context=this;timeoutId=setTimeout(()=>callback.apply(context,arguments_),wait)}}function Logger(prefix,options={}){const baseStyle='font-weight: bold; padding:2px 6px; border-radius: 4px;';const formattedPrefix=`[${prefix}]`;const styles={log:'background: #4caf50; color: white;'+baseStyle,error:'background: #f44336; color: white;'+baseStyle,warn:'background: #ff9800; color: black;'+baseStyle,debug:'background: #2196f3; color: white;'+baseStyle};const icons={log:'‚úÖ',error:'‚ùå',warn:'‚ö†Ô∏è',debug:'üîç'};function format(type,message,...rest){console[type](`%c${icons[type]} ${formattedPrefix}%c ${message}`,styles[type],'',...rest)}function log(message,...rest){format('log',message,...rest)}log.error=function(message,...rest){format('error',message,...rest)};log.warn=function(message,...rest){format('warn',message,...rest)};log.debug=function(message,...rest){if(options.debug){format('debug',message,...rest)}};log.debugEnabled=!!options.debug;return log}if(typeof module!=='undefined'&&module.exports){module.exports={debounce:debounce,Logger:Logger}}if(typeof window!=='undefined'){window.debounce=debounce;window.Logger=Logger}
